import React from 'react'
import currencyFormatter from "currency-formatter";
import {useSelector} from "react-redux";
export const Cashout = () => {
   const {totalPrice} = useSelector(state => state.CartReducer);
   const cashoutSubmit = (e) => {
      e.preventDefault();
      auth.onAuthStateChanged(user => {
          if (user) {
              const date = new Date();
              const time = date.getTime();
              db.collection('Buyer-info ' + user.uid).doc('_' + time).set({
                  BuyerName: name,
                  BuyerEmail: email,
                  BuyerCell: cell,
                  BuyerAddress: address,
                  BuyerPayment: totalPrice,
                  BuyerQuantity: totalQty
              }).then(() => {
                  setCell('');
                  setAddress('');
                  dispatch({ type: 'EMPTY' })
                  setSuccessMsg('Your order has been placed successfully. Thanks for visiting us. You will be redirected to home page after 5 seconds');
                  setTimeout(() => {
                      history.push('/')
                  }, 5000)
              }).catch(err => setError(err.message))
          }
      })
  }
  return (
    <div>
      <form autoComplete="off" className='form-group'>
      <h1>Cashout</h1>
                    <label htmlFor="name">Name</label>
                    <input type="text" className='form-control' required
                       />
                    <br />
                    <label htmlFor="email">Email</label>
                    <input type="email" className='form-control' required
                        />
                    <br />
                    <label htmlFor="Cell No">Số Điện thoại</label>
                    <input type="number" className='form-control' required
                          />
                    <br />
                    <label htmlFor="Delivery Address">Địa chỉ</label>
                    <input type="text" className='form-control' required
                         />
                    <br />
                    <label htmlFor="Price To Pay">Tổng số tiền: </label>
                       {currencyFormatter.format(totalPrice, {code: 'VND'})}
                    <br />
                    <button type="submit" className='submit'>Mua hàng</button>
                </form>
    </div>
  )
}
