import React, { Component } from 'react'
import { Link, Redirect } from 'react-router-dom'

export default class Login extends Component {
    constructor(props){
        super(props)
        const token = localStorage.getItem("token")

        let loggedIn = true
        if(token == null){
            loggedIn = false
        }

        this.state = {
            email: '',
            password: ''
        }
        this.onChange = this.onChange.bind(this)
        this.submitForm = this.submitForm.bind(this)
    }
    onChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    submitForm(e){
        e.preventDefault()
        const {email, password} = this.state
        if(email === "admin@gmail.com" && password === "123456"){
            localStorage.setItem("token", "hgjfdhg")
            this.setState({
                loggedIn: true
            })
        }
    }
    render() {
        if(this.state.loggedIn){
            return <Redirect to = "/admin"/>
        }
        return (
            <div className='login_page'>
                <h1>Login</h1>
                <form onSubmit = {this.submitForm}>
                   <label htmlFor="email">Email</label>
                    <input type = "text" placeholder='email' name = "email" value = {this.state.email} onChange={this.onChange}/>
                    <br/>
                    <label htmlFor="password">Password</label>
                    <input type = "text" placeholder='password' name = "password" value = {this.state.password} onChange={this.onChange}/>
                    <br/>
                    <input type="submit"/>
                </form>
            </div>
        )
    }
}
