import React, {useEffect, useState} from 'react'
import {useParams} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux"
import currencyFormatter from "currency-formatter";
import {BsDash, BsPlus} from "react-icons/bs";
const Details = () => {
    const [quantity, setQuantity] = useState(1);
    const {id} = useParams();
    const dispatch = useDispatch();
    const {product} = useSelector(state => state.ProductsReducer);
    useEffect(() => {
       dispatch({type: 'PRODUCT', id})
    }, [dispatch, id])
    const decQuantity = () => {
        if(quantity > 1) {
            setQuantity(quantity-1)
        }
    }
    return (
        <section>
            <h1 class="heading">Thông tin về sản phẩm</h1>
            <hr class="mx-auto"></hr>
        <div className="container mt-100">
            <div className="row">
                <div className="col-6">
                <div className="details__image">
                    <img src={`/images/${product.productImg}`} alt=""/>
                </div>
                </div>
                <div className="col-6">
                <div className="details__name">
                    {product.productName}
                </div>
                <div className="details__prices">
   Giá sản phẩm:  <span className="details__actaul">{currencyFormatter.format(product.productPrice, { code: 'VND' })}</span>

                </div>
                <div className="details__info">
                    <div className="details__incDec">
                    <span className="dec" onClick={decQuantity}><BsDash /></span>
    <span className="quantity">{quantity}</span>
    <span className="inc" onClick={() => setQuantity(quantity+1)}><BsPlus/></span>
    <button className="btn-default" onClick={() => dispatch({type: 'ADD_TO_CART', payload: {product, quantity} })}>Thêm vào giỏ hàng</button>
    </div>
                </div>
                <div className="details__p">
                    <h4>Thông tin chi tiết về sản phẩm</h4>
                    {product.productDescription}
                </div>
                </div>
            </div>
        </div>
        </section>
    )
}

export default Details
