import React, { Component } from 'react'
import {useHistory} from "react-router-dom"

const Login = () => {
    const history = useHistory()

	const [input, setInput] = React.useState({ email: '', password: '' });

	const [errorMessage, seterrorMessage] = React.useState('');
	const [successMessage, setsuccessMessage] = React.useState('');

	const handleChange = e => {
		setInput({ ...input, [e.target.name]: e.target.value });
	};

	React.useEffect(()=>{
		if(localStorage.getItem('auth')) history.push('/')
	},[])

	const formSubmitter = e => {
		e.preventDefault();
		setsuccessMessage('');
		if (!emailValidator(input.email)) return seterrorMessage('Please enter valid email id');

		if (!passwordValidator(input.password))
			return seterrorMessage(
				'Password should have minimum 8 character with the combination of uppercase, lowercase, numbers and specialcharaters'
			);
		// setsuccessMessage('Successfully Validated');
		if(input.email !== 'admin@a.com' || input.password !== 'Password@1') return seterrorMessage('Invalid email or password');

		history.push('/')
		localStorage.setItem('auth', true)

	};
        return (
            <div className='login_page'>
                <h1>Login</h1>
                <form onSubmit = {this.submitForm}>
                   <label htmlFor="email">Email</label>
                    <input type = "text" placeholder='email' name = "email" value = {this.state.email} onChange={this.onChange}/>
                    <br/>
                    <label htmlFor="password">Password</label>
                    <input type = "text" placeholder='password' name = "password" value = {this.state.password} onChange={this.onChange}/>
                    <br/>
                    <input type="submit"/>
                </form>
            </div>
        )
    }
export default Login;
