import React, {} from 'react'
import {Link} from "react-router-dom";
import {useSelector} from "react-redux";
import { AiOutlineShoppingCart } from "react-icons/ai";
import { useHistory } from 'react-router-dom'

const Nav = (user) => {
    const {totalQuantities} = useSelector(state => state.CartReducer)
    const history = useHistory();

    // handle logout
    const handleLogout = () => {
        auth.signOut().then(() => {
            history.push('/login');
        })
    }
    return (
        <main>
        <nav className="nav">
            <div className="nav-text">
                <img src="/images/logo5.png" alt="logo"/>
                <ul className="nav-menu menu-main">
                    <li><Link to="/">HOME</Link></li>
                    <li><Link to="#">TIN TỨC</Link></li>
                    <li><Link to="#">GIỚI THIỆU</Link></li>
                    <li><Link to="#">LIÊN HỆ</Link></li>
                    {!user && <div className='rightside'>
                        <li><Link to="signup" className='navlink'>SIGN UP</Link></li>
                        <li><Link to="login" className='navlink'>LOGIN</Link></li>
                        </div>}
                    {user && <div className='rightside'>
                        <li><Link to="/" className='navlink'>{user}</Link></li>
                        <li><Link to="cartproducts" className='navlink'></Link></li>
                        <li><button className='logout-btn' onClick={handleLogout}>Logout</button></li>
                    </div>}
                </ul>

                <form action="" className="search-box-container">
                    <input type="search" id="search-box" placeholder="Tìm kiếm....."/>
                    <button>Tìm kiếm</button>
                </form>
                <Link to="/cart">
                    <div className="basket">
                        <AiOutlineShoppingCart className="cart-icon" />
                        <span>{totalQuantities}</span>
                    </div>
                </Link>
            </div>
        </nav>
        </main>
    )
}

export default Nav
