import React, { useState } from 'react'
import axios from 'axios';


function AddProducts (props) {

    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');
    const [productImg, setProductImg] = useState(null);
    const [productDescription, setProductDescription] = useState('');

    const types = ['image/png', 'image/jpeg']; // image types
    const apiUrl = '';

    // add product
    const addProduct = (e) => {
        e.preventDefault();
        axios.post(apiUrl, data)
        .then((resul) =>
        {
            props.history.push('/ProductList')
        })  
    }

    const onChange = (e) => {
        e.persist();
        setProductName({...productName, [e.target.name] : e.target.value});
        setProductPrice({...productPrice, [e.target.name] : e.target.value});
        setProductDescription({...productDescription, [e.target.name] : e.target.value});
        setProductImg({...productImg, [e.target.name] : e.target.value});
    }

    return (
        <div className='add-container'>
            <br />
            <h2>ADD PRODUCTS</h2>
            <hr />
            <form autoComplete="off" className='form-group' onSubmit={addProduct}>
                <label htmlFor="product-name">Product Name</label>
                <input type="text" className='form-control' required
                    onChange={(e) => setProductName(e.target.value)} value={productName} />
                <br />
                <label htmlFor="product-price">Product Price</label>
                <input type="number" className='form-control' required
                    onChange={(e) => setProductPrice(e.target.value)} value={productPrice} />
                <br />
                <label htmlFor="product-description">Product Description</label>
                <input type="text" className='form-control' required
                    onChange={(e) => setProductDescription(e.target.value)} value={productDescription} />
                <br />
                <label htmlFor="product-img">Product Image</label>
                <input type="file" className='form-control' id="file" required
                    onChange={productImgHandler} />
                <br />
                <button type="submit" className='submit'>ADD</button>
            </form>
            {error && <span className='error-msg'>{error}</span>}
        </div>
    )
}
