import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import swal from 'sweetalert'
import { useHistory } from 'react-router-dom'

export const Register = (props) => {

    const history = useHistory();
    const [registerInput, setRegister] = useState({
        name: '',
        email: '',
        password: '',
        error_list: [],
    });

    const handleInput = (e) => {
        e.persist ();
        setRegister({...registerInput, [e.target.name]: e.target.value});
    }

    const registerSubmit = (e) => {
        e.preventDefault();

        const data = {
            name: registerInput.name,
            email: registerInput.email,
            password: registerInput.password,
        }

        axios.get('/sanctum/csrf-cookie').then(response => {
            axios.post(`http://localhost:8000/api/register`, data).then(res => {
                if(res.data.status === 200)
                {
                    localStorage.setItem('auth_token', res.data.token);
                    localStorage.setItem('auth_name', res.data.username);
                    swal("Success", res.data.message, "success");
                    history.push('/');
                }
                else
                {
                    setRegister({...registerInput, error_list: res.data.validation_errors});
                }
            })
        })
    }


    return (
        <div className='Register_page'>
            <br />
            <h2>Register</h2>
            <br />
            <form autoComplete="off" className='form-group' onSubmit={Register}>
                <label htmlFor="name">Name</label>
                <input type="text" className='form-control' required
                    onChange={handleInput}  value={registerInput.name} />
                <br />
                <label htmlFor="email">Email</label>
                <input type="email" className='form-control' required
                    onChange={handleInput}  value={registerInput.email} />
                <br />
                <label htmlFor="passowrd">Password</label>
                <input type="password" className='form-control' required
                    onChange={handleInput}  value={registerInput.password} />
                <br />
                <button type="submit" className='btn btn-success btn-md mybtn'>Đăng ký</button>
            </form>

            <span>Already have an account?  
                <Link to="/login"> Đăng nhập</Link>
            </span>
        </div>
    )
}
export default Register
