import React, { useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios';

function Signup() {

    const history = useHistory();
    const [signupInput, setSignup] = useState({
        name: '',
        email: '',
        password: '',
    });
    const handleInput = (e) => {
        e.presist();
        setSignup({...signupInput, [e.target.name]: e.target.value})
    }

    const signupSubmit = (e) => {
        e.preventDefault();

        const data = {
            name: signupInput.name,
            email: signupInput.email,
            password: signupInput.password,
            error_list: [],
        }
        axios.get( `/sanctum/csrf-cookie`).then(response => {
            axios.post(`/api/signup`, data).then(res =>{
                if(res.data.status === 200)
                {
                    localStorage.setItem('auth_token', res.data.token);
                    localStorage.setItem('auth_token', res.data.usename);
                    swal("Success", res.data.message, "succses");
                    history.pushState('/');
                }
                else
                {
                    setSignup({...signupInput, error_list: res.data.validation_errors});
                }
            })
        })
    }


    return (
        <div className='signup_page'>
            <br />
            <h2>Sign up</h2>
            <br />
            <form autoComplete="off" className='form-group' onSubmit={signupSubmit}>
                <div className='form-control'>
                    <label htmlFor="name">Name</label>
                    <input type="text"
                        onChange={handleInput} value={signupInput.name}/>
                        <span>{signupInput.error_list.name}</span>
                </div>
                <div className='form-control'>
                    <label htmlFor="email">Email</label>
                    <input type="email"
                        onChange={handleInput} value={signupInput.email}/>
                        <span>{signupInput.error_list.email}</span>
                </div>
                <div className='form-control'>
                    <label htmlFor="passowrd">Password</label>
                    <input type="password"
                        onChange={handleInput} value={signupInput.password}/>
                        <span>{signupInput.error_list.password}</span>
                </div>
                <button type="submit" className='btn btn-success btn-md mybtn'>Đăng ký</button>
            </form>
            <span>Already have an account?  
                <Link to="/login"> Đăng nhập</Link>
            </span>
        </div>
    )
}
export default Signup
