import React, { useState } from "react";
import axios from 'axios'
 
//import { useHistory } from "react-router-dom";
 
const AddProduct = () => {
  // let history = useHistory(); // Use for Navigate on Previous
  const [user, setUser] = useState({
    product_name: "",
    product_price: "",
    product_description: ""
  });
 
  const { product_name, product_price, product_description, product_img} = user;
   
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };
   
  const onSubmit = async e => {
    e.preventDefault();
    await axios.post("http://localhost:3000/posts",user);
    alert('Data Inserted');
    // history.push("/");
  };
  return (
    <div className="add-container">
      <div class="row">  
       <div className="form-group">
        <h2 className="text-center mb-4">Add A Product</h2>
        <form onSubmit={e => onSubmit(e)}>
          <div className="form-group">
          <label htmlFor="product-name">Tên sản phẩm</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Product Name"
              name="product_name"
              value={product_name}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
          <label htmlFor="product-price">Giá tiền</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Product Price"
              name="product_price"
              value={product_price}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
          <label htmlFor="product-description">Mô tả về sản phẩm</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Product Description"
              name="product_description"
              value={product_description}
              onChange={e => onInputChange(e)}
            />
          </div>
          <label htmlFor="product-img">Ảnh sản phẩm</label>
                <input type="file" className="form-control" onChange={e => onInputChange(e)}/>
                
                <button className="submit" onClick="onSubmit" >THÊM</button>

          <button className="submit">Add Product</button>
        </form>
      </div>
    </div>
  </div>  
  );
};
 
export default AddProduct;
