import React, { useState } from 'react'
import { auth, db } from '../admin/Config'
import { Link } from 'react-router-dom'
import axios from 'axios';

export const Signup = (props) => {

    const [signupInput, setSignup] = useState({
        name: '',
        email: '',
        password: '',
    });
    const handleInput = (e) => {
        e.presist();
        setSignup({...signupInput, [e.target.name]: e.target.value})
    }

    const signupSubmit = (e) => {
        e.preventDefault();

        const data = {
            name: signupInput.name,
            email: signupInput.email,
            password: signupInput.password,
        }
        axios.get( `/sanctum/csrf-cookie`).then(response => {
            axios.post(`/api/signup`, data).then(res =>{
            })
        })
    }


    return (
        <div className='signup_page'>
            <br />
            <h2>Sign up</h2>
            <br />
            <form autoComplete="off" className='form-group' onSubmit={signupSubmit}>
                <label htmlFor="name">Name</label>
                <input type="text" className='form-control' required
                    onChange={handleInput} value={signupInput.name}/>
                <br />
                <label htmlFor="email">Email</label>
                <input type="email" className='form-control' required
                    onChange={handleInput} value={signupInput.email}/>
                <br />
                <label htmlFor="passowrd">Password</label>
                <input type="password" className='form-control' required
                    onChange={handleInput} value={signupInput.password}/>
                <br />
                <button type="submit" className='btn btn-success btn-md mybtn'>Đăng ký</button>
            </form>
            {error && <span className='error-msg'>{error}</span>}
            <br />
            <span>Already have an account?  
                <Link to="/login"> Đăng nhập</Link>
            </span>
        </div>
    )
}
export default Signup
