import React from 'react'
import axios from 'axios'

class AddProducts extends React.Component
{
    state = {
        productName : '',
        productPrice: '',
        productDescription:'',
        productImg:'',
    }
    handleChange = (e)=>{
        console.log(e.target.name);
        console.log(e.target.value);
        this.setState({[e.target.name]:e.target.value})
    }
    handleSubmit = ()=>{
        if(this.state.productName!=='' && this.state.productPrice!=='',this.state.productDescription!=='',this.state.productImg!=='')
        {
            axios.post('http://localhost:3000/students',this.state)
            .then(res=>{
                console.log('successfully posted');
                this.setState({productName:'',productPrice:'',productDescription:'', productImg:''});
            });
            window.location = '/';
        }
    }
    render ()
    {
    return (
        <div className='add-container'>
            <br />
            <h2>ADD PRODUCTS</h2>
            <hr />
            <form autoComplete="off" className='form-group' onSubmit={()=>this.handleSubmit()}>
                <label htmlFor="product-name">Tên sản phẩm</label>
                <input type="text" className='form-control' required
                    onChange={(e)=>this.handleChange(e)} value={this.state.productName} />
                <br />
                <label htmlFor="product-description">Mô tả sản phẩm</label>
                <input type="text" className='form-control' required
                    onChange={(e)=>this.handleChange(e)} value={this.state.productDescription} />
                <br />
                <label htmlFor="product-price">Giá</label>
                <input type="text" className='form-control' required
                    onChange={(e)=>this.handleChange(e)} value={this.state.productPrice} />
                <br />
                <label htmlFor="product-img">Ảnh sản phẩm</label>
                <input type="file" className='form-control' id="file" required
                    onChange={(e)=>this.handleChange(e)} />
                <br />
                <button type="submit" className='submit'>Thêm</button>
            </form>
        </div>
    )
    }
}
export default AddProducts
