import React, {useState} from 'react'
import { BsPersonSquare } from "react-icons/bs";

const Login = () => {
    const [userRegistrantion, setuserRegistration] = useState({
        username:"",
        password:""
    });
    
    const handleInput = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        console.log(name, value);

        setuserRegistration({... userRegistrantion, [name] : value })
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        const newRecord = {...userRegistrantion}
    }
    return (
        <div className="login_page">
            <form action="" onSubmit = {handleSubmit}>
            <div className="Login">
                <BsPersonSquare className="Login_icon" />
            </div>
            <div className="loginContainer">
                <label htmlFor="usename">Username</label>
                <input
                type="text" autoComplete="off" value={userRegistrantion.username}
                onChange={handleInput} name="username" id="username"
                />
                </div>
                <div>
                <label htmlFor="password">Password</label>
                <input
                type="password" autoComplete="off" value={userRegistrantion.password}
                onChange={handleInput} name="password" id="password"
                />
            </div>
                <div className="row">
                    <button type="submit" className="btn btn-primary">ĐĂNG NHẬP</button>
                </div>
            </form>
        </div>
    )
}

export default Login
