import React, {useState} from 'react'
import { useHistory } from 'react-router';
import { auth } from '../Config/Config';
const Login = () => {
    const history = useHistory();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [errorMsg, setErrorMsg] = useState('');
    const [successMsg, setSuccessMsg] = useState('');

    const handleLogin=(e)=>{
        e.preventDefault();
        auth.signInWithEmailAndPassword(email, password).then(()=>{
            setSuccessMsg('Đăng nhập thành công. Chào mừng bạn quay trở lại !')
            setEmail('');
            setPassword('');
            setErrorMsg('');
            setTimeout(()=>{
                setSuccessMsg('');
                history.push('/');
            }, 3000)
        }).catch(error => setErrorMsg(error.message));
    }
    return (
        <div className ="login_page">
            <br></br>
            <h1>LOGIN</h1>
            <hr></hr>
            <form className='form-group' autoComplete="off"
                onSubmit={handleLogin}>
                <label>Username</label>
                <input
                type="text" autoFocus required value={email}
                onChange={(e) => setEmail(e.target.value)}
                />

                <br></br>
                <lable>Password</lable>
                <input
                type="password" autoFocus required value={password}
                onChange={(e) => setPassword(e.target.value)}
                />
                <br></br>
                <div className="btn-box">
                    <button type="submit" className='btn-md'>Đăng nhập</button>
                </div>
            </form> 
        </div>
    )
}

export default Login
